import React, {useEffect, useState,} from 'react';
import { useParams } from "react-router-dom"



const Dima = () => {
    let {slug} = useParams()
    const [users, setUsers] = useState([])
    const fetchData = async () => {

      const response = await fetch("http://localhost:8000/api2/pages/dima/")
      const data = await response.json()
      setUsers(data)
      }




  useEffect(() => {
    fetchData()
  }, [])




      return (
        <div>
          {users.map(item => (
            <div key={item.id}>
              {item.type === 'image_text' && (
                <div>
                  <img src={item.value.image.url} alt={item.value.image.alt} />
                  <p>{item.value.text}</p>
                </div>
              )}
              {item.type === 'paragraph' && (
                <p dangerouslySetInnerHTML={{ __html: item.value }} />
              )}
              {item.type === 'h1' && <h1>{item.value}</h1>}
            </div>
          ))}
        </div>
      );
}



  export default Dima;
